
public class PrefixOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 7;
		System.out.println("전위 증감연산");
		System.out.println("++num : " + ++num);
		System.out.println("++num : " + ++num);
		System.out.println("num : " + num);
		
		System.out.println("----------------");
		
		System.out.println("후위 증감연산");
		System.out.println("num++ : " + num++);
		System.out.println("num++ : " + num++);
		System.out.println("num : " + num);

	}

}